const breadcrumbs=document.querySelector(".breadcrumbs");function getBreadcrumbs(){let t=location.href.split("/").slice(3),e=[{text:"Home",link:"/"}];for(let r=0;r<t.length;r++){let l=t[r].toLowerCase().replace("-"," ").split(".")[0],s=l[0].toUpperCase()+l.slice(1),n="/"+t.slice(0,r+1).join("/");e.push({text:s,link:n})}return e.length-1?e.map((t=>`\n\t\t\t<li class="breadcrumbs-item">\n\t\t\t\t<a class="breadcrumbs-link breadcrumbs-link--deactive" href="${t.link}">${t.text}</a>\n\t\t\t</li>`)).join(""):e.map((t=>`\n\t\t\t<li class="breadcrumbs-item">\n\t\t\t\t<a class="breadcrumbs-link" href="${t.link}">${t.text}</a>\n\t\t\t</li>`)).join("")}breadcrumbs.innerHTML=getBreadcrumbs();