const breadcrumbs=document.querySelector(".breadcrumbs");function getBreadcrumbs(){let t=[],e=location.href.split("/").slice(3);if(""===e[0])t={text:"Home",link:"/"};else for(let r=0;r<e.length;r++){t.push({text:"Home",link:"/"});let l=e[r].toLowerCase().replace("-"," ").split(".")[0],s=l[0].toUpperCase()+l.slice(1),i="/"+e.slice(0,r+1).join("/");t.push({text:s,link:i})}return t.length-1?t.map((t=>`\n\t\t\t<li class="breadcrumbs-item">\n\t\t\t\t<a class="breadcrumbs-link breadcrumbs-link--deactive" href="${t.link}">${t.text}</a>\n\t\t\t</li>`)).join(""):t.map((t=>`\n\t\t\t<li class="breadcrumbs-item">\n\t\t\t\t<a class="breadcrumbs-link" href="${t.link}">${t.text}</a>\n\t\t\t</li>`)).join("")}breadcrumbs.innerHTML=getBreadcrumbs();